// Verificar si es texto
if ($json.message && $json.message.text) {
  // Formatear texto: min√∫sculas, trim, normalizar espacios
  const textoFormateado = $json.message.text
    .toLowerCase()
    .trim()
    .replace(/\s+/g, ' '); // M√∫ltiples espacios ‚Üí un espacio

  // Crear nuevo objeto con texto formateado
  return {
    ...$json,
    message: {
      ...$json.message,
      text: textoFormateado
    }
  };
}

// Si no es texto, devolver sin cambios
return $json;

// 1. Define la variable 'item' usando los datos del nodo "Leer datos de inasistencia".
//    Reemplaza 'Leer datos de inasistencia' si tu nodo se llama diferente.
const item = $items('Leer datos de inasistencia')[0].json;

// 2. Obtenemos la fecha actual y la formateamos como AAAA-MM-DD.
const fecha = new Date().toISOString().split('T')[0];

// 3. Variable para guardar la ruta del archivo que encontremos.
let filePath = '';

// 4. Intentamos obtener la ruta del nodo de informaci√≥n de ARCHIVOS.
//    Reemplaza 'HTTP Obtener Info del archivo' si tu nodo tiene un nombre diferente.
try {
    const infoArchivoDocArray = $items('HTTP Obtener Info del documento');
    if (infoArchivoDocArray.length > 0 && infoArchivoDocArray[0].json.result && infoArchivoDocArray[0].json.result.file_path) {
        filePath = infoArchivoDocArray[0].json.result.file_path;
    }
} catch (e) {
    // Error esperado si el nodo no se ejecut√≥ (porque se envi√≥ una imagen).
}

// 5. Si no encontramos la ruta en el nodo de archivos, la buscamos en el de IM√ÅGENES.
if (!filePath) {
    try {
        // Reemplaza 'HTTP Obtener Info de la imagen' si tu nodo tiene un nombre diferente.
        const infoArchivoImgArray = $items('HTTP Obtener Info de la imagen');
        if (infoArchivoImgArray.length > 0 && infoArchivoImgArray[0].json.result && infoArchivoImgArray[0].json.result.file_path) {
            filePath = infoArchivoImgArray[0].json.result.file_path;
        }
    } catch (e) {
        // Error esperado si el nodo no se ejecut√≥ (porque se envi√≥ un documento).
    }
}

// 6. Verificamos si 'item' se defini√≥ correctamente antes de usarlo.
if (item && typeof item === 'object') {
    item.ruta_para_descarga = filePath;

    // 7. Obtenemos la extensi√≥n del archivo de forma segura.
    const extension = filePath ? filePath.split('.').pop() : '';

    // 8. Construimos el nombre final del archivo.
    //    Aseg√∫rate de que 'Numero_Session' y 'legajo enviado' existan en 'item'.
    item.nombre_de_archivo_final = `${item.Numero_Session}_${fecha}_${item['legajo enviado']}.${extension}`;
} else {
    // Si 'item' no es un objeto v√°lido, devolvemos un error claro.
    return { error: "No se pudieron obtener los datos del nodo 'Leer datos de inasistencia'. Revisa su salida." };
}

// 9. Devolvemos el item con los nuevos campos.
return item;
